import React, { useState } from "react";
import { StyleSheet } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { GestureHandlerRootView } from "react-native-gesture-handler";
import { KeyboardProvider } from "react-native-keyboard-controller";
import { SafeAreaProvider } from "react-native-safe-area-context";
import { StatusBar } from "expo-status-bar";

import RootNavigator from "@/navigation/RootNavigator";
import { AuthProvider } from "@/hooks/useAuth";
import { ErrorBoundary } from "@/components/ErrorBoundary";

export default function App() {
  const [isNavigationReady, setIsNavigationReady] = useState(false);

  return (
  <ErrorBoundary>
    <SafeAreaProvider>
        <GestureHandlerRootView style={styles.root}>
          <KeyboardProvider>
            <AuthProvider>
              <NavigationContainer
                onReady={() => setIsNavigationReady(true)}
                onStateChange={() => {
                  if (!isNavigationReady) {
                    setIsNavigationReady(true);
                  }
                }}
              >
                <RootNavigator />
              </NavigationContainer>
            </AuthProvider>
            <StatusBar style="dark" />
          </KeyboardProvider>
        </GestureHandlerRootView>
      </SafeAreaProvider>
  </ErrorBoundary>
  );
}

const styles = StyleSheet.create({
  root: {
    flex: 1,
  },
});

components/ScreenFlatList.tsx.content:
import React from "react";
import { FlatList, FlatListProps, StyleSheet } from "react-native";

import { useTheme } from "@/hooks/useTheme";
import { useScreenInsets } from "@/hooks/useScreenInsets";
import { useResponsive } from "@/hooks/useResponsive";

export function ScreenFlatList<T>({
  contentContainerStyle,
  style,
  ...flatListProps
}: FlatListProps<T>) {
  const { theme } = useTheme();
  const { paddingTop, paddingBottom, scrollInsetBottom } = useScreenInsets();

  return (
    <FlatList
      style={[
        styles.container,
        { backgroundColor: theme.backgroundRoot },
        style,
      ]}
      contentContainerStyle={[
        {
          paddingTop,
          paddingBottom,
        },
        contentContainerStyle,
      ]}
      scrollIndicatorInsets={{ bottom: scrollInsetBottom }}
      showsVerticalScrollIndicator={false}
      {...flatListProps}
    />
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
});

screens/auth/LoginScreen.tsx.content:
import React, { useState } from 'react';
import { View, StyleSheet, TextInput, Image, Pressable, Platform, ScrollView } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import { useNavigation } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { ThemedText } from '@/components/ThemedText';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import type { RootStackParamList } from '@/navigation/RootNavigator';

export default function LoginScreen() {
  const navigation = useNavigation<NativeStackNavigationProp<RootStackParamList>>();
  const insets = useSafeAreaInsets();
  const [isLogin, setIsLogin] = useState(true);
  const [phone, setPhone] = useState('');
  const [name, setName] = useState('');

  const handleSendOTP = () => {
    if (isLogin && phone.length === 10) {
      navigation.navigate('OTP', { phone: `+91${phone}`, name: 'User' });
    } else if (!isLogin && phone.length === 10 && name.length > 0) {
      navigation.navigate('OTP', { phone: `+91${phone}`, name });
    }
  };

  const isFormValid = isLogin 
    ? phone.length === 10 
    : phone.length === 10 && name.length > 2;

  return (
    <View style={styles.rootContainer}>
      {/* Top Pink Section - Logo & Tabs */}
      <LinearGradient
        colors={['#FFD4E5', '#FFE5EE']}
        style={[styles.pinkSection, { paddingTop: insets.top }]}
      >
        <View style={styles.logoSection}>
          <Image
            source={require('@/attached_assets/JioKidslogo_1763910976445.png')}
            style={styles.logo}
            resizeMode="contain"
          />
        </View>

        {/* Tab Switcher */}
        <View style={styles.tabContainer}>
          <Pressable 
            style={[styles.tab, isLogin && styles.tabActive]}
            onPress={() => {
              setIsLogin(true);
              setName('');
            }}
          >
            <ThemedText style={[styles.tabText, isLogin && styles.tabTextActive]}>
              Login
            </ThemedText>
            {isLogin && <View style={styles.tabIndicator} />}
          </Pressable>

          <Pressable 
            style={[styles.tab, !isLogin && styles.tabActive]}
            onPress={() => {
              setIsLogin(false);
              setPhone('');
            }}
          >
            <ThemedText style={[styles.tabText, !isLogin && styles.tabTextActive]}>
              Register
            </ThemedText>
            {!isLogin && <View style={styles.tabIndicator} />}
          </Pressable>
        </View>
      </LinearGradient>

      {/* Bottom White Section - Form */}
      {isLogin ? (
        <View style={[styles.whiteSection, { paddingBottom: insets.bottom }]}>
          <View style={styles.formContainer}>
            <ThemedText style={styles.title}>
              Welcome Back
            </ThemedText>
            <ThemedText style={styles.subtitle}>
              Login to continue shopping
            </ThemedText>

            {/* Phone Field */}
            <View style={styles.inputWrapper}>
              <ThemedText style={styles.inputLabel}>Mobile Number</ThemedText>
              <View style={[styles.modernInputContainer, phone.length > 0 && styles.modernInputActive]}>
                <View style={styles.modernInputGradient}>
                  <Feather name="phone" size={20} color="#FF6B9D" style={styles.inputIcon} />
                  <TextInput
                    value={phone}
                    onChangeText={setPhone}
                    placeholder="Enter 10 digit number"
                    keyboardType="phone-pad"
                    maxLength={10}
                    style={styles.modernPhoneInput}
                    placeholderTextColor="#999"
                  />
                </View>
              </View>
              {phone.length > 0 && phone.length < 10 && (
                <ThemedText style={styles.helperText}>
                  {10 - phone.length} more digits needed
                </ThemedText>
              )}
            </View>

            <Pressable
              onPress={handleSendOTP}
              disabled={!isFormValid}
              style={[
                styles.submitButton,
                isFormValid && styles.submitButtonActive
              ]}
            >
              <LinearGradient
                colors={isFormValid ? ['#FF6B9D', '#FF8FB3'] : ['#D0D0D0', '#B8B8B8']}
                style={styles.submitButtonGradient}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 0 }}
              >
                <ThemedText style={[
                  styles.submitButtonText,
                  !isFormValid && styles.submitButtonTextDisabled
                ]}>
                  Continue
                </ThemedText>
              </LinearGradient>
            </Pressable>

            <View style={styles.dividerContainer}>
              <View style={styles.dividerLine} />
              <ThemedText style={styles.dividerText}>OR</ThemedText>
              <View style={styles.dividerLine} />
            </View>

            <Pressable style={styles.socialButton}>
              <ThemedText style={styles.socialButtonText}>
                Continue with Google
              </ThemedText>
            </Pressable>

            <ThemedText style={styles.termsText}>
              By continuing, you agree to our{'\n'}
              <ThemedText style={styles.termsLink}>Terms of Service</ThemedText>
              {' and '}
              <ThemedText style={styles.termsLink}>Privacy Policy</ThemedText>
            </ThemedText>
          </View>
        </View>
      ) : (
        <ScrollView style={[styles.whiteSection, { paddingBottom: insets.bottom }]} contentContainerStyle={styles.scrollContent}>
          <View style={styles.formContainer}>
            <ThemedText style={styles.title}>
              Create Account
            </ThemedText>
            <ThemedText style={styles.subtitle}>
              Register to start shopping
            </ThemedText>

            {/* Name Field for Register */}
            <View style={styles.inputWrapper}>
              <ThemedText style={styles.inputLabel}>Full Name</ThemedText>
              <View style={[styles.modernInputContainer, name.length > 0 && styles.modernInputActive]}>
                <View style={styles.modernInputGradient}>
                  <Feather name="user" size={20} color="#FF6B9D" style={styles.inputIcon} />
                  <TextInput
                    value={name}
                    onChangeText={setName}
                    placeholder="Enter your full name"
                    style={styles.modernPhoneInput}
                    placeholderTextColor="#999"
                  />
                </View>
              </View>
            </View>

            {/* Phone Field */}
            <View style={styles.inputWrapper}>
              <ThemedText style={styles.inputLabel}>Mobile Number</ThemedText>
              <View style={[styles.modernInputContainer, phone.length > 0 && styles.modernInputActive]}>
                <View style={styles.modernInputGradient}>
                  <Feather name="phone" size={20} color="#FF6B9D" style={styles.inputIcon} />
                  <TextInput
                    value={phone}
                    onChangeText={setPhone}
                    placeholder="Enter 10 digit number"
                    keyboardType="phone-pad"
                    maxLength={10}
                    style={styles.modernPhoneInput}
                    placeholderTextColor="#999"
                  />
                </View>
              </View>
              {phone.length > 0 && phone.length < 10 && (
                <ThemedText style={styles.helperText}>
                  {10 - phone.length} more digits needed
                </ThemedText>
              )}
            </View>

            <Pressable
              onPress={handleSendOTP}
              disabled={!isFormValid}
              style={[
                styles.submitButton,
                isFormValid && styles.submitButtonActive
              ]}
            >
              <LinearGradient
                colors={isFormValid ? ['#FF6B9D', '#FF8FB3'] : ['#D0D0D0', '#B8B8B8']}
                style={styles.submitButtonGradient}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 0 }}
              >
                <ThemedText style={[
                  styles.submitButtonText,
                  !isFormValid && styles.submitButtonTextDisabled
                ]}>
                  Register
                </ThemedText>
              </LinearGradient>
            </Pressable>

            <View style={styles.dividerContainer}>
              <View style={styles.dividerLine} />
              <ThemedText style={styles.dividerText}>OR</ThemedText>
              <View style={styles.dividerLine} />
            </View>

            <Pressable style={styles.socialButton}>
              <ThemedText style={styles.socialButtonText}>
                Continue with Google
              </ThemedText>
            </Pressable>

            <ThemedText style={styles.termsText}>
              By continuing, you agree to our{'\n'}
              <ThemedText style={styles.termsLink}>Terms of Service</ThemedText>
              {' and '}
              <ThemedText style={styles.termsLink}>Privacy Policy</ThemedText>
            </ThemedText>
          </View>
        </ScrollView>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  rootContainer: {
