import { ThemedText } from '@/components/ThemedText';
import { Colors, Spacing, BorderRadius } from '@/constants/theme';

export default function NotificationsScreen() {
  const notifications = [
    { 
      id: '1', 
      title: 'Payment Successful', 
      message: 'Your payment of ₹2,999 was successful. Order #12345', 
      type: 'payment', 
      read: false 
    },
    { 
      id: '2', 
      title: 'Order Confirmed', 
      message: 'Your order #12345 has been confirmed and is being processed', 
      type: 'order', 
      read: false 
    },
    { 
      id: '3', 
      title: 'Order Delivered', 
      message: 'Your order #12340 has been delivered successfully', 
      type: 'order', 
      read: true 
    },
    { 
      id: '4', 
      title: 'Flash Sale', 
      message: 'Up to 60% OFF on kids fashion. Limited time offer!', 
      type: 'promo', 
      read: false 
    },
    { 
      id: '5', 
      title: 'Order Shipped', 
      message: 'Your order #12338 is on the way', 
      type: 'order', 
      read: true 
    },
  ];

  const getIcon = (type: string) => {
    switch (type) {
      case 'payment':
        return 'check-circle';
      case 'order':
        return 'package';
      case 'promo':
        return 'tag';
      default:
        return 'bell';
    }
  };

  const getIconColor = (type: string) => {
    switch (type) {
      case 'payment':
        return Colors.light.success;
      case 'order':
        return Colors.light.primary;
      case 'promo':
        return Colors.light.warning;
      default:
        return Colors.light.textGray;
