hooks/useAuth.tsx.content:
import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface User {
  id: string;
  name: string;
  email?: string;
  phone?: string;
  avatar?: string;
}

interface AuthContextType {
  user: User | null;
  isLoading: boolean;
  hasSeenOnboarding: boolean;
  signIn: (user: User) => Promise<void>;
  signOut: () => Promise<void>;
  completeOnboarding: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

const USER_KEY = '@jiokidz_user';
const ONBOARDING_KEY = '@jiokidz_onboarding';

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [hasSeenOnboarding, setHasSeenOnboarding] = useState(false);

  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      const [userData, onboardingData] = await Promise.all([
        AsyncStorage.getItem(USER_KEY),
        AsyncStorage.getItem(ONBOARDING_KEY),
      ]);

      if (userData) {
        setUser(JSON.parse(userData));
      }

      if (onboardingData) {
        setHasSeenOnboarding(true);
      }

      // Keep splash screen visible for full animation duration (~12 seconds)
      setTimeout(() => {
        setIsLoading(false);
      }, 12000);
    } catch (error) {
      console.error('Failed to load user data:', error);
      setTimeout(() => {
        setIsLoading(false);
      }, 12000);
    }
  };

  const signIn = async (userData: User) => {
    try {
      await AsyncStorage.setItem(USER_KEY, JSON.stringify(userData));
      setUser(userData);
    } catch (error) {
      console.error('Failed to save user data:', error);
      throw error;
    }
  };

  const signOut = async () => {
    try {
      await AsyncStorage.removeItem(USER_KEY);
      setUser(null);
    } catch (error) {
      console.error('Failed to sign out:', error);
      throw error;
    }
  };

  const completeOnboarding = async () => {
    try {
      await AsyncStorage.setItem(ONBOARDING_KEY, 'true');
      setHasSeenOnboarding(true);
    } catch (error) {
      console.error('Failed to save onboarding status:', error);
      throw error;
    }
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isLoading,
        hasSeenOnboarding,
        signIn,
        signOut,
        completeOnboarding,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

utils/storage.ts.content:
import AsyncStorage from '@react-native-async-storage/async-storage';
import { CartItem, Address, Product } from '@/data/types';

const CART_KEY = '@jiokidz_cart';
const WISHLIST_KEY = '@jiokidz_wishlist';
const ADDRESSES_KEY = '@jiokidz_addresses';

export const cartStorage = {
  async getCart(): Promise<CartItem[]> {
    try {
      const data = await AsyncStorage.getItem(CART_KEY);
      return data ? JSON.parse(data) : [];
    } catch (error) {
      console.error('Failed to load cart:', error);
      return [];
    }
  },

  async addToCart(item: CartItem): Promise<void> {
    try {
      const cart = await this.getCart();
      const existingIndex = cart.findIndex(
        i => i.product.id === item.product.id &&
        i.selectedSize === item.selectedSize &&
        i.selectedColor === item.selectedColor
      );

      if (existingIndex >= 0) {
        cart[existingIndex].quantity += item.quantity;
      } else {
        cart.push(item);
      }

      await AsyncStorage.setItem(CART_KEY, JSON.stringify(cart));
    } catch (error) {
      console.error('Failed to add to cart:', error);
      throw error;
    }
  },

  async updateQuantity(itemId: string, quantity: number): Promise<void> {
    try {
      const cart = await this.getCart();
      const index = cart.findIndex(i => i.id === itemId);

      if (index >= 0) {
        if (quantity <= 0) {
          cart.splice(index, 1);
        } else {
          cart[index].quantity = quantity;
        }
        await AsyncStorage.setItem(CART_KEY, JSON.stringify(cart));
      }
    } catch (error) {
      console.error('Failed to update cart:', error);
      throw error;
    }
  },

  async removeFromCart(itemId: string): Promise<void> {
    try {
      const cart = await this.getCart();
      const filtered = cart.filter(i => i.id !== itemId);
      await AsyncStorage.setItem(CART_KEY, JSON.stringify(filtered));
    } catch (error) {
      console.error('Failed to remove from cart:', error);
      throw error;
    }
  },

  async clearCart(): Promise<void> {
    try {
      await AsyncStorage.removeItem(CART_KEY);
    } catch (error) {
      console.error('Failed to clear cart:', error);
      throw error;
    }
  },
};

export const wishlistStorage = {
  async getWishlist(): Promise<Product[]> {
    try {
      const data = await AsyncStorage.getItem(WISHLIST_KEY);
      return data ? JSON.parse(data) : [];
    } catch (error) {
      console.error('Failed to load wishlist:', error);
      return [];
    }
  },

  async addToWishlist(product: Product): Promise<void> {
    try {
      const wishlist = await this.getWishlist();
      if (!wishlist.find(p => p.id === product.id)) {
        wishlist.push(product);
        await AsyncStorage.setItem(WISHLIST_KEY, JSON.stringify(wishlist));
      }
    } catch (error) {
      console.error('Failed to add to wishlist:', error);
      throw error;
    }
  },

  async removeFromWishlist(productId: string): Promise<void> {
    try {
      const wishlist = await this.getWishlist();
      const filtered = wishlist.filter(p => p.id !== productId);
      await AsyncStorage.setItem(WISHLIST_KEY, JSON.stringify(filtered));
    } catch (error) {
      console.error('Failed to remove from wishlist:', error);
      throw error;
    }
  },

  async isInWishlist(productId: string): Promise<boolean> {
    try {
      const wishlist = await this.getWishlist();
      return wishlist.some(p => p.id === productId);
    } catch (error) {
      console.error('Failed to check wishlist:', error);
      return false;
    }
  },
};

export const addressStorage = {
  async getAddresses(): Promise<Address[]> {
    try {
      const data = await AsyncStorage.getItem(ADDRESSES_KEY);
      return data ? JSON.parse(data) : [];
    } catch (error) {
      console.error('Failed to load addresses:', error);
      return [];
    }
  },

  async saveAddress(address: Address): Promise<void> {
    try {
      const addresses = await this.getAddresses();
      const index = addresses.findIndex(a => a.id === address.id);

      if (index >= 0) {
        addresses[index] = address;
      } else {
        addresses.push(address);
      }

      if (address.isDefault) {
        addresses.forEach(a => {
          if (a.id !== address.id) {
            a.isDefault = false;
          }
        });
      }

      await AsyncStorage.setItem(ADDRESSES_KEY, JSON.stringify(addresses));
    } catch (error) {
      console.error('Failed to save address:', error);
      throw error;
    }
  },

  async deleteAddress(addressId: string): Promise<void> {
    try {
      const addresses = await this.getAddresses();
      const filtered = addresses.filter(a => a.id !== addressId);
      await AsyncStorage.setItem(ADDRESSES_KEY, JSON.stringify(filtered));
    } catch (error) {
      console.error('Failed to delete address:', error);
      throw error;
    }
  },
};

