import React, { useState, useRef, useEffect } from 'react';
import { View, StyleSheet, TextInput, Pressable, Platform } from 'react-native';
import { useNavigation, useRoute, RouteProp } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { LinearGradient } from 'expo-linear-gradient';
import { Feather } from '@expo/vector-icons';
import { ThemedText } from '@/components/ThemedText';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useAuth } from '@/hooks/useAuth';
import type { RootStackParamList } from '@/navigation/RootNavigator';

export default function OTPScreen() {
  const navigation = useNavigation<NativeStackNavigationProp<RootStackParamList>>();
  const route = useRoute<RouteProp<RootStackParamList, 'OTP'>>();
  console.log('OTPScreen rendered with params:', route.params);
  const { signIn } = useAuth();
  const insets = useSafeAreaInsets();
  const [otp, setOtp] = useState(['', '', '', '', '', '']);
  const [timer, setTimer] = useState(30);
  const [isVerifying, setIsVerifying] = useState(false); // New state for loading
  const [errorMessage, setErrorMessage] = useState(''); // New state for error messages
  const inputRefs = useRef<Array<TextInput | null>>([]);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleOtpChange = (value: string, index: number) => {
    setErrorMessage(''); // Clear error message on OTP change
    if (value.length <= 1 && /^\d*$/.test(value)) {
      const newOtp = [...otp];
      newOtp[index] = value;
      setOtp(newOtp);

      if (value && index < 5) {
        inputRefs.current[index + 1]?.focus();
      }
    }
  };

  const handleKeyPress = (key: string, index: number) => {
    if (key === 'Backspace' && !otp[index] && index > 0) {
      inputRefs.current[index - 1]?.focus();
    }
  };

  const handleVerify = async () => {
    const otpValue = otp.join('');
    if (otpValue.length === 6) {
      setIsVerifying(true);
      setErrorMessage('');

      // Simulate API call for OTP verification
      await new Promise(resolve => setTimeout(resolve, 2000)); // Simulate 2 second delay

      setIsVerifying(false);

      // Simulate successful verification (e.g., if OTP is '123456')
      // In a real app, you would send this to your backend for verification
      if (otpValue === '123456') { // Example successful OTP
        await signIn({
          id: '1',
          name: route.params.name || 'User',
          phone: route.params.phone,
        });
        // Navigation will be handled by RootNavigator based on user state
      } else {
        setErrorMessage('Invalid OTP. Please try again.');
      }
    } else {
      setErrorMessage('Please enter a 6-digit OTP.');
    }
  };

  const handleResend = () => {
    setTimer(30);
    setOtp(['', '', '', '', '', '']);
    inputRefs.current[0]?.focus();
    setErrorMessage('');
  };

  const allDigitsFilled = otp.join('').length === 6;

  return (
    <View style={styles.rootContainer}>
      {/* Top Pink Section */}
      <LinearGradient
        colors={['#FFE8D6', '#FFF3E0']}
        style={[styles.pinkSection, { paddingTop: insets.top }]}
      >
        <View style={styles.headerContent}>
          <Pressable 
            onPress={() => navigation.goBack()}
            style={styles.backButton}
            hitSlop={{ top: 12, bottom: 12, left: 12, right: 12 }}
          >
            <Feather name="arrow-left" size={24} color="#FF8C00" />
          </Pressable>
          <ThemedText style={styles.headerTitle}>Verify OTP</ThemedText>
          <View style={styles.spacer} />
        </View>
      </LinearGradient>

      {/* Bottom White Section */}
      <View style={[styles.whiteSection, { paddingBottom: insets.bottom }]}>
        <View style={styles.contentContainer}>
          <ThemedText style={styles.title}>
            Enter 6-Digit Code
          </ThemedText>
          <ThemedText style={styles.subtitle}>
            We've sent it to{'\n'}
            <ThemedText style={styles.phoneNumber}>{route.params.phone}</ThemedText>
          </ThemedText>

          {/* OTP Boxes */}
          <View style={styles.otpContainer}>
            {otp.map((digit, index) => (
              <View
                key={index}
                style={[
                  styles.otpBoxWrapper,
                  digit && styles.otpBoxFilled,
                  errorMessage && styles.otpBoxError, // Add error style
                ]}
              >
                <TextInput
                  ref={(ref) => {
                    if (ref) inputRefs.current[index] = ref;
                  }}
                  style={styles.otpInput}
                  value={digit}
                  onChangeText={(value) => handleOtpChange(value, index)}
                  onKeyPress={({ nativeEvent }) => handleKeyPress(nativeEvent.key, index)}
                  keyboardType="number-pad"
                  maxLength={1}
                  autoFocus={index === 0}
                  placeholderTextColor="#DDD"
                  editable={!isVerifying} // Disable input during verification
                />
              </View>
            ))}
          </View>

          {errorMessage ? (
            <ThemedText style={styles.errorText}>{errorMessage}</ThemedText>
          ) : null}

          {/* Timer or Resend */}
          <View style={styles.timerContainer}>
            {timer > 0 ? (
              <>
                <ThemedText style={styles.timerLabel}>Resend code in</ThemedText>
                <ThemedText style={styles.timerValue}>
                  00:{timer.toString().padStart(2, '0')}
                </ThemedText>
              </>
            ) : (
              <Pressable onPress={handleResend} style={styles.resendButton} disabled={isVerifying}>
                <Feather name="refresh-cw" size={16} color="#FF8C00" />
                <ThemedText style={styles.resendText}>Resend Code</ThemedText>
              </Pressable>
            )}
          </View>

          {/* Verify Button */}
          <Pressable
            onPress={handleVerify}
            disabled={!allDigitsFilled || isVerifying} // Disable button while verifying
            style={[
              styles.verifyButton,
              (allDigitsFilled && !isVerifying) && styles.verifyButtonActive
            ]}
          >
            <LinearGradient
              colors={(allDigitsFilled && !isVerifying) ? ['#FF8C00', '#FF8FB3'] : ['#D0D0D0', '#B8B8B8']}
              style={styles.verifyButtonGradient}
              start={{ x: 0, y: 0 }}
              end={{ x: 1, y: 0 }}
            >
              <ThemedText style={[
                styles.verifyButtonText,
                (!allDigitsFilled || isVerifying) && styles.verifyButtonTextDisabled
              ]}>
                {isVerifying ? 'Verifying...' : 'Verify OTP'}
              </ThemedText>
            </LinearGradient>
          </Pressable>

          {/* Info Text */}
          <ThemedText style={styles.infoText}>
            Didn't receive the code?{'\n'}
            Check your spam folder
          </ThemedText>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  rootContainer: {
    flex: 1,
    backgroundColor: '#FFFFFF',
  },
  pinkSection: {
    flex: 0.25,
    borderBottomLeftRadius: 24,
    borderBottomRightRadius: 24,
    overflow: 'hidden',
    paddingBottom: 16,
  },
  backButton: {
    padding: 12,
    marginLeft: -12,
  },
  headerContent: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingBottom: 12,
  },
  headerTitle: {
    fontSize: 18,
    fontWeight: '700',
    color: '#FF8C00',
    flex: 1,
    textAlign: 'center',
  },
  spacer: {
    width: 24,
  },
  whiteSection: {
    flex: 0.75,
    backgroundColor: '#FFFFFF',
  },
  contentContainer: {
    flex: 1,
    paddingHorizontal: 24,
    paddingTop: 24,
    justifyContent: 'flex-start',
  },
  title: {
    fontSize: 24,
    fontWeight: '700',
    color: '#1F2937',
    marginBottom: 8,
    letterSpacing: -0.5,
  },
  subtitle: {
    fontSize: 13,
    color: '#6B7280',
    marginBottom: 28,
    lineHeight: 20,
  },
  phoneNumber: {
    color: '#FF8C00',
    fontWeight: '700',
  },
  otpContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    gap: 10,
    marginBottom: 32,
  },
  otpBoxWrapper: {
    width: 50,
    height: 56,
    borderRadius: 14,
    borderWidth: 2,
    borderColor: '#FFD4A3',
    backgroundColor: 'rgba(255, 182, 217, 0.1)',
    overflow: 'hidden',
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: '#FF8C00',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.08,
    shadowRadius: 4,
    elevation: 2,
  },
  otpBoxFilled: {
    borderColor: '#FF8C00',
    backgroundColor: 'rgba(255, 107, 157, 0.05)',
    shadowOpacity: 0.15,
    elevation: 3,
  },
  otpBoxError: { // New style for error state
    borderColor: '#EF4444', // Red border
    shadowColor: '#EF4444',
  },
  otpInput: {
    width: '100%',
    height: '100%',
    fontSize: 24,
    fontWeight: '800',
    textAlign: 'center',
    color: '#1F2937',
  },
  timerContainer: {
    alignItems: 'center',
    marginBottom: 24,
    paddingVertical: 12,
    backgroundColor: 'rgba(255, 182, 217, 0.1)',
    borderRadius: 12,
  },
  timerLabel: {
    fontSize: 12,
    color: '#6B7280',
    fontWeight: '500',
  },
  timerValue: {
    fontSize: 20,
