    image: '👶',
  },
  {
    id: '3',
    name: 'Educational Toy Bundle',
    price: '₹2,499',
    originalPrice: '₹3,999',
    rating: 4.7,
    reviews: 945,
    image: '🧩',
  },
];

interface BestSellersCarouselProps {
  onProductPress?: (id: string) => void;
}

export function BestSellersCarousel({ onProductPress }: BestSellersCarouselProps) {
  const handleAddToCart = async (itemId: string) => {
    try {
      const product = PRODUCTS.find(p => p.id === itemId);
      if (product && product.name && product.price) {
        const cartItem = {
          id: itemId,
          product,
          quantity: 1,
          selectedSize: 'M',
          selectedColor: '#FF8C00'
        };
        await cartStorage.addToCart(cartItem);
        Alert.alert('Success', `${product.name} added to cart!`);
      } else {
        Alert.alert('Error', 'Product information incomplete');
      }
    } catch (error) {
      Alert.alert('Error', 'Failed to add item to cart');
    }
  };

  const renderCard = ({ item }: any) => (
    <Pressable
      style={styles.card}
      onPress={() => onProductPress?.(item.id)}
    >
      <LinearGradient
        colors={['#FFF3E0', '#FFFAF5']}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
