import React from 'react';
import { StyleSheet, View, ScrollView, FlatList, Dimensions } from 'react-native';
import { useRoute } from '@react-navigation/native';

import { ThemedText } from '@/components/ThemedText';
import { TopHeader } from '@/components/TopHeader';
import { useTheme } from '@/hooks/useTheme';
import { Spacing, BorderRadius, Colors } from '@/constants/theme';
import { PRODUCTS, CATEGORIES } from '@/data/mockData';
import { Product } from '@/data/types';
import { ProductCardDetails } from '@/components/ProductCardDetails';

const { width: screenWidth } = Dimensions.get('window');

// Function to chunk array into smaller arrays
const chunkArray = (array: any[], chunkSize: number) => {
  const result = [];
  for (let i = 0; i < array.length; i += chunkSize) {
    result.push(array.slice(i, i + chunkSize));
  }
  return result;
};

export default function CategoryDetailsScreen() {
  const route = useRoute<any>();
  const { theme } = useTheme();
  const { categoryName } = route.params || { categoryName: 'Category' };

  // Filter products relevant to the category
  const categoryProducts = PRODUCTS.filter(product => product.category === categoryName);

  // Define section titles and prepare product data for each section
  const sections = [
    { title: "Top Picks", products: categoryProducts.slice(0, 8) },
    { title: "New Arrivals", products: categoryProducts.slice(8, 16) }, // Assuming enough mock data
    { title: "Trending Now", products: categoryProducts.slice(16, 24) },
    { title: "Best Value", products: categoryProducts.slice(24, 32) },
  ].filter(section => section.products.length > 0); // Only show sections with products

  // If not enough products for 8 per section, duplicate or show fewer
  const ensureEightProducts = (prods: Product[]): Product[] => {
    if (prods.length >= 8) return prods.slice(0, 8);
    // Duplicate existing products to ensure at least 8, if fewer are available
    const repeated = Array(8).fill(null).map((_, i) => prods[i % prods.length]).filter(Boolean) as Product[];
    return repeated.slice(0,8);
  };

  const sectionsWithEnoughProducts = sections.map(section => ({
    ...section,
    products: ensureEightProducts(section.products),
  }));

  const handleProductPress = (product: Product) => {
    // Navigate to product detail screen or perform other action
    console.log('Product pressed:', product.name);
  };

  const renderProductCarouselPage = ({ item }: { item: Product[] }) => (
    <View style={styles.carouselPage}>
      <View style={styles.carouselRow}>
        {item.slice(0, 4).map((product, index) => (
          <View key={product.id + '-row1-' + index} style={styles.productCardWrapper}>
            <ProductCardDetails product={product} onPress={handleProductPress} />
          </View>
        ))}
      </View>
      <View style={styles.carouselRow}>
        {item.slice(4, 8).map((product, index) => (
          <View key={product.id + '-row2-' + index} style={styles.productCardWrapper}>
            <ProductCardDetails product={product} onPress={handleProductPress} />
          </View>
        ))}
      </View>
    </View>
  );

  return (
    <View style={{ flex: 1, backgroundColor: theme.backgroundRoot }}>
      <TopHeader showBackButton={true} />
      <ScrollView style={[styles.container, { backgroundColor: theme.backgroundRoot }]} showsVerticalScrollIndicator={false}>
        {/* Category Header */}
        <View style={styles.headerContainer}>
          <ThemedText type="h1" style={styles.categoryTitle}>{categoryName}</ThemedText>
          <ThemedText type="body" style={styles.categorySubtitle}>Explore top products in this category</ThemedText>
        </View>

      {/* Product Sections */}
      {sectionsWithEnoughProducts.map((section, index) => (
        <View key={index} style={styles.sectionContainer}>
          <ThemedText type="h2" style={styles.sectionTitle}>{section.title}</ThemedText>
          <FlatList
            horizontal
            pagingEnabled
            showsHorizontalScrollIndicator={false}
            data={chunkArray(section.products, 8)} // Each page will now have 8 products (4x2 grid)
            renderItem={renderProductCarouselPage}
            keyExtractor={(item, idx) => `section-${index}-page-${idx}`}
            snapToInterval={screenWidth - Spacing.md * 2}
            decelerationRate="fast"
            contentContainerStyle={styles.flatListContentContainer}
          />
        </View>
      ))}
      <View style={{ height: Spacing.xxl }} />
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  headerContainer: {
    padding: Spacing.xl,
    paddingBottom: Spacing.md,
    borderBottomWidth: 1,
    borderBottomColor: Colors.light.border,
  },
  categoryTitle: {
    fontSize: 24,
    fontWeight: '700',
    color: Colors.light.text,
    marginBottom: Spacing.xs,
  },
  categorySubtitle: {
    fontSize: 14,
    fontWeight: '400',
    color: Colors.light.textGray,
  },
  sectionContainer: {
    marginTop: Spacing.xl,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: '700',
    color: Colors.light.text,
    marginBottom: Spacing.md,
    paddingHorizontal: Spacing.md,
  },
  flatListContentContainer: {
    paddingHorizontal: Spacing.md, // Padding around the entire FlatList content
  },
  carouselPage: {
    width: screenWidth - Spacing.md * 2, // Width of one page, accounting for horizontal padding
    justifyContent: 'center',
  },
  carouselRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: Spacing.sm, // Space between rows
  },
  productCardWrapper: {
    width: (screenWidth - Spacing.md * 2 - Spacing.sm * 3) / 4, // 4 cards + 3 spaces between them
  },
});

screens/auth/SimpleSplashScreen.tsx.content:
import React, { useEffect } from 'react';
import { View, StyleSheet, Image, Dimensions } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import Animated, {
  useSharedValue,
  useAnimatedStyle,
  withRepeat,
  withTiming,
  withSpring,
  Easing,
  interpolate,
} from 'react-native-reanimated';
import { LinearGradient } from 'expo-linear-gradient';
import { Colors } from '@/constants/theme';

const { width, height } = Dimensions.get('window');

function FloatingCircle({ delay, size, position }: any) {
  const floatValue = useSharedValue(0);

  useEffect(() => {
    floatValue.value = withRepeat(
      withTiming(1, {
        duration: 3000 + delay,
        easing: Easing.inOut(Easing.ease),
      }),
      -1,
      true
    );
  }, []);

  const animatedStyle = useAnimatedStyle(() => ({
    transform: [
      {
        translateY: interpolate(floatValue.value, [0, 1], [0, -30]),
      },
    ],
    opacity: interpolate(floatValue.value, [0, 0.5, 1], [0.3, 0.6, 0.3]),
  }));

  return (
    <Animated.View
      style={[
        {
          position: 'absolute',
          ...position,
          width: size,
          height: size,
          borderRadius: size / 2,
          backgroundColor: 'rgba(255, 255, 255, 0.15)',
        },
        animatedStyle,
      ]}
    />
  );
}

export default function SimpleSplashScreen({ onFinish }: { onFinish: () => void }) {
  const rotateValue = useSharedValue(0);
  const scaleValue = useSharedValue(0.8);
  const opacityValue = useSharedValue(0);
  const jumpValue = useSharedValue(0);

  useEffect(() => {
    // Animate opacity and scale to bring the logo in with a spring effect
    opacityValue.value = withTiming(1, { duration: 400, easing: Easing.ease });
    scaleValue.value = withSpring(1, { damping: 12, stiffness: 90 });

    // Rotate with a spring for a more professional and dynamic effect
    rotateValue.value = withSpring(360 * 2, {
      damping: 18,
      stiffness: 90,
      mass: 1.2,
    });

    // Use requestAnimationFrame fallback for web compatibility
    let timer: ReturnType<typeof setTimeout>;
    const startTime = Date.now();
    const checkTime = () => {
      if (Date.now() - startTime >= 3000) {
        onFinish();
      } else {
        timer = setTimeout(checkTime, 100);
      }
    };
    timer = setTimeout(checkTime, 100);

    return () => clearTimeout(timer);
  }, [onFinish, opacityValue, scaleValue, rotateValue]);

  const animatedLogoStyle = useAnimatedStyle(() => ({
    transform: [
      { rotate: `${rotateValue.value}deg` },
      { scale: scaleValue.value },
    ],
    opacity: opacityValue.value,
  }));

  return (
    <View style={styles.container}>
      <LinearGradient
        colors={['#FF8C00', '#FFD4A3', '#FFE8D6', '#FFF8F0']}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
        style={styles.gradient}
      >
        {/* Top Right Circles */}
        <FloatingCircle
          delay={0}
          size={100}
          position={{ top: -30, right: -20 }}
        />
        <FloatingCircle
          delay={500}
          size={60}
          position={{ top: 40, right: 10 }}
        />
        <FloatingCircle
          delay={200}
          size={45}
          position={{ top: 100, right: width * 0.15 }}
        />

        {/* Bottom Left Circles */}
        <FloatingCircle
          delay={800}
          size={80}
          position={{ bottom: -20, left: -30 }}
        />
        <FloatingCircle
          delay={300}
          size={50}
          position={{ bottom: 50, left: 20 }}
        />
        <FloatingCircle
          delay={600}
          size={70}
          position={{ bottom: 80, left: width * 0.15 }}
        />

        {/* Top Left Circles */}
        <FloatingCircle
          delay={400}
          size={55}
          position={{ top: height * 0.2, left: -15 }}
        />

        {/* Bottom Right Circles */}
        <FloatingCircle
          delay={700}
          size={65}
          position={{ bottom: 30, right: width * 0.12 }}
        />

        {/* Center Decorative Elements */}
        <View
          style={[
            styles.decorativeCircle,
            {
              top: height * 0.15,
              right: width * 0.1,
              backgroundColor: 'rgba(255, 255, 255, 0.08)',
            },
          ]}
        />
        <View
          style={[
            styles.decorativeCircle,
            {
              bottom: height * 0.2,
              left: width * 0.08,
              width: 120,
              height: 120,
              backgroundColor: 'rgba(155, 89, 182, 0.08)',
            },
          ]}
        />
        <View
          style={[
            styles.decorativeCircle,
            {
              top: height * 0.4,
              left: width * 0.05,
              width: 100,
              height: 100,
              backgroundColor: 'rgba(255, 182, 217, 0.06)',
            },
          ]}
        />
        <View
          style={[
            styles.decorativeCircle,
            {
              bottom: height * 0.1,
              right: width * 0.08,
              width: 110,
              height: 110,
              backgroundColor: 'rgba(255, 107, 157, 0.06)',
            },
          ]}
        />

        {/* Main Logo Container */}
        <View style={styles.content}>
          <Animated.View style={[styles.logoContainer, animatedLogoStyle]}>
            <Image
              source={require('@/attached_assets/JioKidslogo_1763923777175.png')}
              style={styles.logo}
              resizeMode="contain"
            />
          </Animated.View>
        </View>
      </LinearGradient>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  gradient: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    overflow: 'hidden',
  },
  content: {
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 10,
  },
  logoContainer: {
    width: 220,
    height: 220,
    justifyContent: 'center',
    alignItems: 'center',
  },
  logo: {
    width: '100%',
    height: '100%',
  },
  decorativeCircle: {
    position: 'absolute',
    width: 150,
    height: 150,
    borderRadius: 75,
  },
});

screens/auth/AppIntroCarousel.tsx.content:
import React, { useRef, useState } from 'react';
import {
  View,
  StyleSheet,
  ScrollView,
  Dimensions,
  Pressable,
  NativeSyntheticEvent,
  NativeScrollEvent,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { ThemedText } from '@/components/ThemedText';
import { Feather } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import { Colors, Spacing, BorderRadius } from '@/constants/theme';

const { width, height } = Dimensions.get('window');

interface Slide {
  id: number;
  title: string;
  description: string;
  icon: string;
  gradient: [string, string];
}

const slides: Slide[] = [
  {
    id: 1,
    title: 'Welcome to Jio Kidz',
    description:
      'Premium quality products for your little ones. Everything from toys to clothing, all in one place.',
    icon: 'gift',
    gradient: ['#FF8C00', '#FFD4A3'],
  },
  {
    id: 2,
    title: 'Safe & Trusted',
    description:
      'All products are verified and tested for safety. We care about your child\'s wellbeing and happiness.',
    icon: 'shield',
    gradient: ['#9B59B6', '#D8BFD8'],
  },
  {
    id: 3,
    title: 'Fast Delivery',
    description:
      'Quick checkout and reliable delivery. Track your order in real-time and get it delivered to your doorstep.',
    icon: 'truck',
    gradient: ['#3498DB', '#A8E6CF'],
  },
];

export default function AppIntroCarousel() {
  const navigation = useNavigation<any>();
  const scrollViewRef = useRef<ScrollView>(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleScroll = (event: NativeSyntheticEvent<NativeScrollEvent>) => {
    const scrollX = event.nativeEvent.contentOffset.x;
    const slideIndex = Math.round(scrollX / width);
    setCurrentSlide(slideIndex);
  };

  const handleNext = () => {
    if (currentSlide < slides.length - 1) {
      scrollViewRef.current?.scrollTo({
        x: (currentSlide + 1) * width,
        animated: true,
      });
    } else {
      navigation.replace('Onboarding');
    }
  };

  const handleSkip = () => {
    navigation.replace('Login');
  };

  return (
    <View style={styles.container}>
      <ScrollView
        ref={scrollViewRef}
        horizontal
        pagingEnabled
        scrollEventThrottle={16}
        onScroll={handleScroll}
        showsHorizontalScrollIndicator={false}
      >
        {slides.map((slide) => (
          <LinearGradient
            key={slide.id}
            colors={slide.gradient}
            start={{ x: 0, y: 0 }}
            end={{ x: 1, y: 1 }}
            style={styles.slide}
          >
            <View style={styles.content}>
              <View style={styles.iconContainer}>
                <Feather
                  name={slide.icon as any}
                  size={80}
                  color="#FFFFFF"
                  strokeWidth={1}
                />
              </View>

              <ThemedText
                style={[styles.title, { color: '#FFFFFF' }]}
                type="h1"
              >
                {slide.title}
              </ThemedText>

              <ThemedText style={[styles.description, { color: '#FFFFFF' }]}>
                {slide.description}
              </ThemedText>
            </View>
          </LinearGradient>
        ))}
      </ScrollView>

      <View style={styles.footer}>
        <View style={styles.dotsContainer}>
          {slides.map((_, index) => (
            <View
              key={index}
              style={[
                styles.dot,
                index === currentSlide && styles.activeDot,
              ]}
            />
          ))}
        </View>

        <View style={styles.buttonsContainer}>
          <Pressable onPress={handleSkip} style={styles.skipButton}>
            <ThemedText style={styles.skipText}>
              {currentSlide === slides.length - 1 ? 'Login' : 'Skip'}
            </ThemedText>
          </Pressable>

          <Pressable
            onPress={handleNext}
            style={[
              styles.nextButton,
              { backgroundColor: Colors.light.primary },
            ]}
          >
            <ThemedText style={styles.nextText}>
              {currentSlide === slides.length - 1 ? 'Get Started' : 'Next'}
            </ThemedText>
            <Feather
              name="arrow-right"
              size={18}
              color="#FFFFFF"
              style={styles.buttonIcon}
            />
          </Pressable>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: Colors.light.backgroundRoot,
  },
  slide: {
    width,
    height: height * 0.75,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: Spacing.xxl,
  },
  content: {
    alignItems: 'center',
