import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  TextInput,
  Alert,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { TopHeader } from '@/components/TopHeader';

const ORANGE_PRIMARY = '#FF6B00';
const ORANGE_LIGHT = '#FFF3E6';

export default function AddBankAccountScreen() {
  const navigation = useNavigation();
  const insets = useSafeAreaInsets();

  const [accountNumber, setAccountNumber] = useState('');
  const [ifscCode, setIfscCode] = useState('');
  const [accountHolderName, setAccountHolderName] = useState('');
  const [bankName, setBankName] = useState('');
  const [branchName, setBranchName] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');

  const [showOtpSection, setShowOtpSection] = useState(false);
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);

  const handleGetOtp = () => {
    if (!accountNumber || !ifscCode || !accountHolderName || !bankName || !mobileNumber) {
      Alert.alert('Error', 'Please fill all required fields');
      return;
    }

    // Validate account number (basic validation)
    if (accountNumber.length < 9 || accountNumber.length > 18) {
      Alert.alert('Error', 'Please enter a valid account number');
      return;
    }

    // Validate IFSC code
    if (ifscCode.length !== 11) {
      Alert.alert('Error', 'IFSC code should be 11 characters');
      return;
    }

    // Validate mobile number
    if (mobileNumber.length !== 10) {
      Alert.alert('Error', 'Please enter a valid 10-digit mobile number');
      return;
    }

    setOtpSent(true);
    setShowOtpSection(true);
    Alert.alert('OTP Sent', `OTP has been sent to ${mobileNumber}`);
  };

  const handleVerifyOtp = () => {
    if (!otp || otp.length !== 6) {
      Alert.alert('Error', 'Please enter a valid 6-digit OTP');
      return;
    }

    // Simulate OTP verification
    Alert.alert(
      'Success',
      'Bank account added successfully!',
      [
        {
          text: 'OK',
          onPress: () => navigation.goBack(),
        },
      ]
    );
  };

  return (
    <View style={styles.container}>
      <TopHeader 
        showBackButton={true}
        hideProfileIcon={true}
      />

      <ScrollView style={[styles.content, { marginTop: 90 }]} showsVerticalScrollIndicator={false}>
        <View style={styles.infoBox}>
          <Ionicons name="shield-checkmark" size={20} color={ORANGE_PRIMARY} />
          <Text style={styles.infoText}>
            Your bank details are encrypted and stored securely
          </Text>
        </View>

        <View style={styles.formCard}>
          <Text style={styles.formTitle}>Bank Account Details</Text>

          <View style={styles.inputGroup}>
            <Text style={styles.inputLabel}>Account Holder Name *</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter account holder name"
              value={accountHolderName}
              onChangeText={setAccountHolderName}
              autoCapitalize="words"
            />
          </View>

          <View style={styles.inputGroup}>
            <Text style={styles.inputLabel}>Account Number *</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter account number"
              value={accountNumber}
              onChangeText={setAccountNumber}
              keyboardType="numeric"
              maxLength={18}
            />
          </View>

          <View style={styles.inputGroup}>
            <Text style={styles.inputLabel}>IFSC Code *</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter IFSC code"
              value={ifscCode}
              onChangeText={(text) => setIfscCode(text.toUpperCase())}
              autoCapitalize="characters"
              maxLength={11}
            />
            <Text style={styles.inputHint}>Example: SBIN0001234</Text>
          </View>

          <View style={styles.inputGroup}>
            <Text style={styles.inputLabel}>Bank Name *</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter bank name"
              value={bankName}
              onChangeText={setBankName}
            />
          </View>

          <View style={styles.inputGroup}>
            <Text style={styles.inputLabel}>Branch Name</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter branch name (optional)"
              value={branchName}
              onChangeText={setBranchName}
            />
          </View>

          <View style={styles.inputGroup}>
            <Text style={styles.inputLabel}>Mobile Number *</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter mobile number"
