      description: "Get 15% off on all products",
      discount: "15% OFF",
      minOrder: "₹799",
      validTill: "12 Dec, 2025",
      terms: [
        "Valid for 24 hours only",
        "Maximum discount ₹300",
        "Valid on all categories",
      ],
      type: "expiring",
      icon: "sparkles",
    },
    {
      id: "6",
      code: "WEEKEND25",
      title: "Weekend Bonanza",
      description: "Get 25% off on orders above ₹2499",
      discount: "25% OFF",
      minOrder: "₹2499",
      validTill: "31 Dec, 2025",
      terms: [
        "Valid on weekends only",
        "Maximum discount ₹750",
        "Valid on home & kitchen",
      ],
      type: "high",
      icon: "gift",
    },
    {
      id: "7",
      code: "FIRST50",
      title: "First Order Deal",
      description: "Get ₹50 off on your first order",
      discount: "₹50 OFF",
      minOrder: "₹399",
      validTill: "31 Dec, 2025",
      terms: ["Valid for new users only", "Minimum order ₹399", "One time use"],
      type: "all",
      icon: "pricetag",
    },
    {
      id: "8",
      code: "SUPER300",
      title: "Super Saver",
      description: "Get ₹300 off on orders above ₹2999",
      discount: "₹300 OFF",
      minOrder: "₹2999",
      validTill: "28 Dec, 2025",
      terms: [
        "Valid on all products",
        "Cannot be combined with other offers",
        "Limited period offer",
      ],
      type: "high",
      icon: "wallet",
    },
  ];

  const filteredCoupons = coupons.filter((coupon) => {
    const matchesSearch =
      coupon.code.toLowerCase().includes(searchQuery.toLowerCase()) ||
      coupon.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      coupon.description.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesFilter =
      activeFilter === "all" || coupon.type === activeFilter;

    return matchesSearch && matchesFilter;
  });

  const handleApplyCoupon = (code: string) => {
    setAppliedCoupon(code);
    Alert.alert(
      "Success",
      `Coupon ${code} applied successfully! Proceed to checkout to see the discount.`,
    );
  };

  const handleCopyCode = (code: string) => {
    setCopiedCode(code);
    setTimeout(() => setCopiedCode(null), 2000);
    Alert.alert("Copied!", `Coupon code ${code} copied to clipboard`);
  };

  const getIconName = (iconType: string): keyof typeof Ionicons.glyphMap => {
    switch (iconType) {
      case "pricetag":
        return "pricetag";
      case "gift":
        return "gift";
      case "wallet":
        return "wallet";
      case "sparkles":
        return "sparkles";
      default:
        return "pricetag";
    }
  };

  return (
    <SafeAreaView style={[styles.container, { paddingTop: insets.top }]}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backBtn}
        >
          <Ionicons name="arrow-back" size={24} color="#fff" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Discount Coupons</Text>
        <View style={{ width: 40 }} />
      </View>

      {/* Header Banner */}
      <View style={styles.headerBanner}>
        <View style={styles.headerBannerContent}>
          <View style={styles.headerIconContainer}>
            <Ionicons name="gift" size={20} color="#fff" />
          </View>
          <Text style={styles.headerBannerText}>
            Apply these coupons to get discounts on your orders
          </Text>
        </View>
        <View style={styles.couponCountBadge}>
          <Ionicons name="pricetag" size={16} color="#fff" />
          <Text style={styles.couponCountText}>
            {filteredCoupons.length} Coupons Available
          </Text>
        </View>
      </View>

      {/* Search and Filters */}
      <View style={styles.searchFilterContainer}>
        {/* Search Bar */}
        <View style={styles.searchBar}>
          <Ionicons name="search" size={20} color="#9CA3AF" />
          <TextInput
            style={styles.searchInput}
            placeholder="Search coupons..."
            placeholderTextColor="#9CA3AF"
            value={searchQuery}
            onChangeText={setSearchQuery}
          />
        </View>

        {/* Filter Tabs */}
        <ScrollView
          horizontal
          showsHorizontalScrollIndicator={false}
          style={styles.filterScroll}
        >
          <TouchableOpacity
            onPress={() => setActiveFilter("all")}
            style={[
              styles.filterTab,
              activeFilter === "all" && styles.filterTabActive,
            ]}
          >
            <Text
              style={[
                styles.filterTabText,
                activeFilter === "all" && styles.filterTabTextActive,
              ]}
            >
              All Coupons
            </Text>
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => setActiveFilter("expiring")}
            style={[
              styles.filterTab,
              activeFilter === "expiring" && styles.filterTabExpiring,
            ]}
          >
            <Text
              style={[
                styles.filterTabText,
                activeFilter === "expiring" && styles.filterTabTextActive,
              ]}
            >
              Expiring Soon
            </Text>
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => setActiveFilter("high")}
            style={[
              styles.filterTab,
              activeFilter === "high" && styles.filterTabHigh,
            ]}
          >
            <Text
              style={[
                styles.filterTabText,
                activeFilter === "high" && styles.filterTabTextActive,
              ]}
            >
              High Discounts
            </Text>
          </TouchableOpacity>
        </ScrollView>
      </View>

      {/* Coupons List */}
      <ScrollView
        style={styles.couponsList}
        showsVerticalScrollIndicator={false}
      >
        {filteredCoupons.length === 0 ? (
          <View style={styles.emptyState}>
            <View style={styles.emptyIconContainer}>
              <Ionicons name="bag-handle-outline" size={48} color="#FF6B35" />
            </View>
            <Text style={styles.emptyTitle}>No Coupons Found</Text>
            <Text style={styles.emptyText}>
              Try adjusting your search or filter to find available coupons
            </Text>
          </View>
        ) : (
          filteredCoupons.map((coupon) => {
            const isExpanded = expandedCoupon === coupon.id;
            const isApplied = appliedCoupon === coupon.code;
            const isCopied = copiedCode === coupon.code;

            return (
              <View
                key={coupon.id}
                style={[
                  styles.couponCard,
                  isApplied && styles.couponCardApplied,
                ]}
              >
                {/* Discount Badge */}
                <View style={styles.discountBadge}>
                  <Text style={styles.discountBadgeText}>
                    {coupon.discount}
                  </Text>
                </View>

                {/* Coupon Content */}
                <View style={styles.couponContent}>
                  <View style={styles.couponHeader}>
                    <View style={styles.couponIconContainer}>
                      <Ionicons
                        name={getIconName(coupon.icon)}
                        size={24}
                        color="#FF6B35"
                      />
                    </View>
                    <View style={styles.couponInfo}>
                      <Text style={styles.couponTitle}>{coupon.title}</Text>
                      <Text style={styles.couponDescription}>
                        {coupon.description}
                      </Text>
                    </View>
                  </View>

                  {/* Coupon Code Box */}
                  <View style={styles.codeBox}>
                    <View style={styles.codeBoxLeft}>
                      <Text style={styles.codeLabel}>Coupon Code</Text>
                      <Text style={styles.codeText}>{coupon.code}</Text>
                    </View>
                    <TouchableOpacity
                      onPress={() => handleCopyCode(coupon.code)}
                      style={styles.copyBtn}
                    >
                      <Ionicons
                        name={isCopied ? "checkmark" : "copy-outline"}
                        size={18}
                        color="#FF6B35"
                      />
                      <Text style={styles.copyBtnText}>
                        {isCopied ? "Copied" : "Copy"}
                      </Text>
                    </TouchableOpacity>
                  </View>

                  {/* Validity */}
                  <View style={styles.validityRow}>
                    <Ionicons
                      name="calendar-outline"
                      size={16}
                      color="#F97316"
                    />
                    <Text style={styles.validityText}>
                      Valid till: {coupon.validTill}
                    </Text>
                  </View>

                  {/* View Details */}
                  <TouchableOpacity
                    onPress={() =>
                      setExpandedCoupon(isExpanded ? null : coupon.id)
                    }
                    style={styles.detailsBtn}
                  >
                    <Ionicons
                      name="information-circle-outline"
                      size={18}
                      color="#FF6B35"
                    />
                    <Text style={styles.detailsBtnText}>
                      {isExpanded ? "Hide Details" : "View Details"}
                    </Text>
                  </TouchableOpacity>

                  {/* Expanded Terms */}
                  {isExpanded && (
                    <View style={styles.termsContainer}>
                      <Text style={styles.termsTitle}>Terms & Conditions:</Text>
                      {coupon.terms.map((term, index) => (
                        <View key={index} style={styles.termRow}>
                          <Text style={styles.termBullet}>•</Text>
                          <Text style={styles.termText}>{term}</Text>
                        </View>
                      ))}
                    </View>
                  )}

                  {/* Apply Button */}
                  <TouchableOpacity
                    onPress={() => handleApplyCoupon(coupon.code)}
                    style={[
                      styles.applyBtn,
                      isApplied && styles.applyBtnApplied,
                    ]}
                    disabled={isApplied}
                  >
                    <Text
                      style={[
                        styles.applyBtnText,
                        isApplied && styles.applyBtnTextApplied,
                      ]}
                    >
                      {isApplied ? "Applied ✓" : "Apply Coupon"}
                    </Text>
                  </TouchableOpacity>
                </View>
              </View>
            );
          })
        )}
        <View style={{ height: 40 }} />
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
